 *
 * This file is generated by Composer.
 * You should run 'composer install' to generate this file.
 *
 * @package    AI_SEO_Pro
 */

// If Composer autoload exists, require it
if ( file_exists( __DIR__ . '/autoload_real.php' ) ) {
	require_once __DIR__ . '/autoload_real.php';
	return ComposerAutoloaderInit::getLoader();
}

// Fallback manual autoloader if Composer not installed
spl_autoload_register( function ( $class ) {
	// Project-specific namespace prefix
	$prefix = 'AI_SEO_Pro\\';

	// Base directory for the namespace prefix
	$base_dir = dirname( __DIR__ ) . '/includes/';

	// Does the class use the namespace prefix?
	$len = strlen( $prefix );
	if ( strncmp( $prefix, $class, $len ) !== 0 ) {
		// No, move to the next registered autoloader
		return;
	}

	// Get the relative class name
	$relative_class = substr( $class, $len );

	// Replace namespace separators with directory separators
	// and append with .php
	$file = $base_dir . str_replace( '\\', '/', $relative_class ) . '.php';

	// If the file exists, require it
	if ( file_exists( $file ) ) {
		require $file;
	}
} );